mapper.select:
SELECT RH.rentalNo, RH.rentalType, RH.suplyHoCo, RH.pblancId, RH.houseSn, RH.sttusNm, RH.pblancNm, RH.suplyInsttNm, RH.houseTyNm, RH.suplyTyNm, RH.beforePblancId, RH.rcritPblancDe, RH.przwnerPresnatnDe, RH.refrnc, RH.url, RH.pcUrl, RH.mobileUrl, RH.hsmpNm, RH.brtcNm, RH.signguNm, RH.fullAdres, RH.rnCodeNm, RH.refrnLegaldongNm, RH.pnu, RH.heatMthdNm, RH.totHshldCo, RH.sumSuplyCo, RH.rentGtn, RH.enty, RH.prtpay, RH.surlus, RH.mtRntchrg, RH.beginDe, RH.endDe
, (SELECT COUNT(*) FROM CLIP_RENTALHOUSE CR WHERE RH.rentalNo = CR.rentalNo AND memNo = #{memNo}) as isClip
FROM RentalHouse RH LEFT OUTER JOIN CLIP_RENTALHOUSE CR on (CR.rentalNo = RH.rentalNo) WHERE CR.memNo = #{memNo};
