mapper.select:

WHERE 1 = 1

	AND (

			r.RCP_PARTS_DTLS LIKE '%${item}%'

	)


	AND (

			r.RCP_PAT2 LIKE '%${item}%'

	)


	AND convert(r.INFO_ENG,signed) < ${infoEng}


AND( 
	r.RCP_NM LIKE '%${all}%'
	OR r.RCP_PARTS_DTLS LIKE '%${all}%'
	OR r.RCP_PAT2 LIKE '%${all}%'
)

ORDER BY r.rcpNo DESC LIMIT ${limit} OFFSET ${offset}

SELECT COUNT(*) FROM recipe WHERE 1 = 1
AND (
RCP_PARTS_DTLS LIKE '%${item}%'
)
AND (
RCP_PAT2 LIKE '%${item}%'
)
AND convert(INFO_ENG,signed) < ${infoEng}
AND( RCP_NM LIKE '%${all}%' OR RCP_PARTS_DTLS LIKE '%${all}%' OR RCP_PAT2 LIKE '%${all}%' )

SELECT rcpNo, memNo FROM CLIP_RCP
WHERE memNo = ${memNo}
LIMIT ${limit} OFFSET ${offset}

SELECT rcpNo, memNo FROM CLIP_RCP
rcpNo = ${rcpNo}
AND memNo = ${memNo}

SELECT COUNT(*) FROM REVIEW_RCP
WHERE status = 'Y'
AND rcpNo = ${rcpNo}

SELECT rcpNo, memNo FROM LIKE_RECIPE
WHERE rcpNo = ${rcpNo}
AND memNo = ${memNo}

mapper.insert:
INSERT INTO CLIP_RCP(
	rcpNo, memNo
) VALUES (
	#{rcpNo}, #{memNo}
	 )

INSERT INTO LIKE_RECIPE(
	rcpNo, memNo
) VALUES (
	#{rcpNo}, #{memNo}
	 )

mapper.delete:
DELETE FROM CLIP_RCP 
WHERE rcpNo = ${rcpNo}
AND memNo = ${memNo}

DELETE FROM LIKE_RECIPE 
WHERE rcpNo = ${rcpNo}
AND memNo = ${memNo}
